<script lang="ts">
import { Component, Prop, Vue } from 'vue-facing-decorator';
import { FontAwesomeIcon } from '@fortawesome/vue-fontawesome';

type LogoType = 'name_contrast' |
    'name_green' |
    'icon_contrast' |
    'icon_green' |
    'signature-vertical' |
    'signature-vertical_green' |
    'signature-horizontal' |
    'signature-horizontal_green';

@Component({
    name: 'Logo',
    components: {
        FontAwesomeIcon
    },
    expose: [
        'setType'
    ]
})
export default class Logo extends Vue {
    @Prop
    private type: LogoType;

    private logoType = 'name';
    private logoColor = 'contrast';

    public setType(type: LogoType): void {
        if (type) {
            const [logoType, logoColor] = type.split('_');
            this.logoType = logoType;
            this.logoColor = logoColor;
        }
    }

    public mounted(): void {
        this.setType(this.type);
    }
}
